mapper.update:
UPDATE userinfo SET username = #{username}, useremail = #{useremail}, 
	userphone = #{userphone}

	 ,img = #{img}

WHERE userid = #{userid}

mapper.select:
SELECT * FROM (SELECT ROWNUM AS N , userId , no , userPw , userName , userEmail , userPhone , mRole , img FROM (SELECT * FROM USERINFO
AND username like '%'||#{username}||'%'
ORDER BY no ASC)) WHERE N BETWEEN #{start} AND #{end}
